05_rag_sachbearbeiter_queries.query:
MATCH (norm:LegalNorm)
WHERE NOT EXISTS {
  MATCH (norm)-[:HAS_CHUNK]->(:Chunk)
}

MATCH (doc:LegalDocument)-[:HAS_STRUCTURE]->()-[:CONTAINS_NORM]->(norm)

RETURN 
  doc.sgb_nummer as SGB,
  norm.enbez as Paragraph_ohne_Chunks,
  norm.titel as Titel
LIMIT 20;
